mapper.select:
SELECT ANSWER
  FROM QUIZ_USER_ANSWER
 WHERE QUIZ_ID = #{quizId}
   AND USER_ID = #{esntlId}
 ORDER BY ID DESC LIMIT 1

SELECT FINISH_YN
  FROM KEPCO_GROUP_QUIZ_INFO
 WHERE GROUP_ID = #{groupId}
   AND QUIZ_ID = #{quizId}


SELECT ANSWER_CN
  FROM SRG_EXAM_MST
 WHERE SRG_EXAM_ID = #{faqId}
